	lasttask=task.New(self,function()
		do
			local _h_scaling=(1-(0.8))/2 local _t_scaling=(1+(0.8))/2 local scaling=_h_scaling*sin(1)+_t_scaling local _w_scaling=1 local _d_w_scaling=2.8
			for _=1,_infinite do
