				return true
			end
			if player.x > 136 and player.x < 224 and plantIndex == 5 then
				lstg.var.highlightedPlant = plantIndex
				return true
			end
			return false
		end
		if lstg.var.selectedStage == 4 then
			if player.x > -224 and player.x < -149.4 and plantIndex == 1 then
				lstg.var.highlightedPlant = plantIndex
				return true
