	if lstg.var.selectedStage == 1 or lstg.var.selectedStage == 2 then
		for i = 1, 4 do
			if lstg.var.plantCard[i] ~= 0 then
				New(self.plantShot[lstg.var.plantCard[i]], -224 + 112/2 + (112 * (i - 1)), -240,_)
